/* Pallete to test new combinations with
	rgb(147,223,184)
	rgb(235,220,200)
	rgb(238,237,219)
	rgb(227,170,214)
	rgb(181,216,235)
	rgb(238,242,230)
	rgb(255,237,201)
	rgb(255,200,186)
	rgb(37,41,42)
	rgb(161,219,178)
	rgb(230,230,220)
	rgb(218,221,251)
	rgb(248,218,251)
	rgb(255,171,171)
	rgb(15,219,159)
	rgb(218,251,248)
	rgb(221,229,254)
	rgb(226,255,230)
*/
.project-root-header {
  -webkit-user-drag: element;
}
.tree-view {
  contain: size;
  overflow: auto;
  z-index: 2;
  -webkit-user-select: none;
  display: flex;
  flex-direction: column;
  /* Drag and Drop */
}
.tree-view .tree-view-root {
  padding-left: 5px;
  padding-right: 10px;
  background-color: inherit;
  /*
     * Force a new stacking context to prevent a large, duplicate paint layer from
     * being created for tree-view's scrolling contents that can make the cost of
     * layer tree updates scale at 3x the size of the layer rather than the
     * optimal 1x.
     *
     * On high resolution displays, Chromium handles layers for scrolling content
     * differently and inadvertently creates a duplicate paint layer the size of
     * .tree-view-scroller because descendants of the scroller overlap the
     * auto-created layer.
     */
  isolation: isolate;
  flex-grow: 1;
  position: relative;
  min-width: min-content;
}
.tree-view .header {
  position: relative;
}
.tree-view .tree-view-root .list-tree {
  position: static;
}
.tree-view .entry::before {
  content: '';
  position: absolute;
}
.tree-view .placeholder {
  position: absolute;
  left: 5px;
  padding: 0;
  z-index: 999;
  display: inline-block;
  width: calc(100% - 5px);
  background: #0f82e6;
  list-style: none;
  pointer-events: none;
}
.tree-view .placeholder:before {
  content: "";
  position: absolute;
  height: 2px;
  margin: -1px;
  padding: 0;
  width: inherit;
  background: inherit;
}
.tree-view .placeholder:after {
  content: "";
  position: absolute;
  left: 0;
  margin-top: -2px;
  margin-left: -1px;
  width: 4px;
  height: 4px;
  background: #0f82e6;
  border-radius: 4px;
  border: 1px solid transparent;
}
.tree-view .placeholder:first-child {
  margin-top: 1px;
}
