/* Pallete to test new combinations with
	rgb(147,223,184)
	rgb(235,220,200)
	rgb(238,237,219)
	rgb(227,170,214)
	rgb(181,216,235)
	rgb(238,242,230)
	rgb(255,237,201)
	rgb(255,200,186)
	rgb(37,41,42)
	rgb(161,219,178)
	rgb(230,230,220)
	rgb(218,221,251)
	rgb(248,218,251)
	rgb(255,171,171)
	rgb(15,219,159)
	rgb(218,251,248)
	rgb(221,229,254)
	rgb(226,255,230)
*/
.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  text-align: center;
  font-size: 1.25em;
  line-height: 1.4;
  padding: 4em;
  color: #9fa5b1;
  background-color: #2e3138;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .input-checkbox {
  margin-top: -0.2em;
}
.about .group-start {
  margin-top: 4em;
}
.about .group-item {
  margin-top: 1.5em;
}
.about-container {
  width: 100%;
  max-width: 500px;
}
.about-atom-io:hover .about-logo {
  color: #40a977;
}
.about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 1em auto;
  color: #d8dadf;
  transition: color 0.2s;
}
.about-version-container:hover {
  color: #c9cdd4;
}
.about-version-container:active {
  color: #f4f4f6;
}
.about-version {
  margin-right: .5em;
  font-size: 1.25em;
  vertical-align: middle;
}
.about-more-version {
  color: rgba(159, 165, 177, 0.6);
  font-size: .9em;
}
.about-header-release-notes {
  vertical-align: middle;
  margin-left: 1em;
}
.about-updates {
  width: 100%;
  max-width: 500px;
}
.about-updates-box {
  display: flex;
  align-items: center;
  padding: 10px;
  border: 1px solid #1b1d22;
  border-radius: 6px;
  background-color: #373b44;
}
.about-updates-status {
  flex: 1;
  margin-left: .5em;
  text-align: left;
}
.about-updates-item,
.about-default-update-message .about-updates-label {
  display: block;
}
.about-updates-label {
  color: rgba(159, 165, 177, 0.6);
}
.about-updates-label.is-strong {
  color: #9fa5b1;
}
.about-updates-version {
  margin: 0 .4em;
}
.about-updates-release-notes,
.about-updates-instructions {
  margin: 0 1em 0 1.5em;
}
.about-auto-updates {
  margin-top: 1em;
}
.about-auto-updates input {
  margin-right: .5em;
}
.about-love .icon::before {
  position: relative;
  width: auto;
  height: auto;
  margin-right: 0;
  font-size: 1.5em;
  vertical-align: text-top;
}
.about-love .icon-logo-github::before {
  font-size: 3.6em;
  height: .36em;
}
.about-credits {
  color: rgba(159, 165, 177, 0.6);
}
.about-release-notes {
  color: #0f82e6;
}
.about-release-notes:hover {
  color: #4ea6f4;
}
