/* Pallete to test new combinations with
	rgb(147,223,184)
	rgb(235,220,200)
	rgb(238,237,219)
	rgb(227,170,214)
	rgb(181,216,235)
	rgb(238,242,230)
	rgb(255,237,201)
	rgb(255,200,186)
	rgb(37,41,42)
	rgb(161,219,178)
	rgb(230,230,220)
	rgb(218,221,251)
	rgb(248,218,251)
	rgb(255,171,171)
	rgb(15,219,159)
	rgb(218,251,248)
	rgb(221,229,254)
	rgb(226,255,230)
*/
atom-text-editor .line.github-editorCommentHighlight {
  background-color: #2c4e69;
}
atom-text-editor .line.github-editorCommentHighlight.cursor-line {
  background-color: rgba(33, 163, 241, 0.76);
}
.gutter[gutter-name="github-comment-icon"] {
  width: 1.4em;
}
.github-editorCommentGutterIcon.decoration {
  width: 100%;
}
.github-editorCommentGutterIcon.react-atom-decoration {
  width: 100%;
  text-align: center;
  padding: 0 2.5px;
}
.github-editorCommentGutterIcon button {
  padding: 0;
  border: none;
  background: none;
  vertical-align: middle;
}
.github-EditorComment-omitted {
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #1d1f21;
  border-radius: 2px;
  padding: 10px;
  margin: 5px 0;
}
.github-EditorComment-omitted p {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin: 2.5px;
}
.github-EditorComment-omitted button {
  margin-left: 10px;
}
