css:
* {
  box-sizing: border-box;
}
html {
  font-size: 12px;
}
atom-workspace {
  background-color: #282b47;
}
body {
  font-size: 12px;
  color: #a3a7cb;
  background-color: #282b47;
}
atom-pane {
  border-right: 1px solid #313557;
}
atom-pane:last-child {
  border-right: none;
}
.btn {
  color: #b6b9d6;
  background-color: #35395e;
  height: 2.618em;
}
.btn:focus,
.btn:hover {
  color: #bea8ce;
  background-color: #4b5185;
}
.btn:active {
  color: rgba(211, 177, 192, 0.66);
  background-color: #313557;
}
.btn.selected {
  color: #d3b1c0;
  background-color: #4b5185;
}
.btn:focus,
.btn:focus:active {
  outline: none;
}
.btn.btn-primary {
  background-color: #785394;
  color: #c5b1d3;
}
.btn.btn-primary:focus,
.btn.btn-primary:hover {
  background-color: #835aa1;
}
.btn.btn-primary:active {
  background-color: #6d4c87;
}
.btn.btn-primary.selected {
  background-color: #835aa1;
}
.btn.btn-primary.selected:focus,
.btn.btn-primary.selected:hover {
  background-color: #8d66a9;
}
.btn.btn-info {
  background-color: #785394;
  color: #c0d8f7;
}
.btn.btn-info:focus,
.btn.btn-info:hover {
  background-color: #835aa1;
}
.btn.btn-info:active {
  background-color: #6d4c87;
}
.btn.btn-info.selected {
  background-color: #835aa1;
}
.btn.btn-info.selected:focus,
.btn.btn-info.selected:hover {
  background-color: #8d66a9;
}
.btn.btn-success {
  background-color: #55753c;
  color: #e4ec65;
}
.btn.btn-success:focus,
.btn.btn-success:hover {
  background-color: #5f8243;
}
.btn.btn-success:active {
  background-color: #4b6835;
}
.btn.btn-success.selected {
  background-color: #5f8243;
}
.btn.btn-success.selected:focus,
.btn.btn-success.selected:hover {
  background-color: #69904a;
}
.btn.btn-warning {
  background-color: #ab632d;
  color: #d5e464;
}
.btn.btn-warning:focus,
.btn.btn-warning:hover {
  background-color: #bb6c31;
}
.btn.btn-warning:active {
  background-color: #9b5a29;
}
.btn.btn-warning.selected {
  background-color: #bb6c31;
}
.btn.btn-warning.selected:focus,
.btn.btn-warning.selected:hover {
  background-color: #ca7637;
}
.btn.btn-error {
  background-color: #d64444;
  color: #f6c7b7;
}
.btn.btn-error:focus,
.btn.btn-error:hover {
  background-color: #da5555;
}
.btn.btn-error:active {
  background-color: #d23333;
}
.btn.btn-error.selected {
  background-color: #da5555;
}
.btn.btn-error.selected:focus,
.btn.btn-error.selected:hover {
  background-color: #dd6565;
}
.btn-group > .btn {
  border-color: rgba(0, 0, 0, 0.2);
}
.settings-view .package-card .meta-controls .status-indicator {
  width: 5px;
}
atom-text-editor[mini] {
  padding-left: 7.5px;
  color: #a3a7cb;
  border-radius: 3px;
  background-color: #262943;
}
atom-text-editor[mini].is-focused {
  background-color: #1d1f33;
}
atom-text-editor[mini] .placeholder-text {
  color: #7278af;
}
atom-text-editor[mini] .selection .region {
  background-color: #35395e;
}
.find-and-replace {
  border-top: 1px solid #313557;
  padding: 0px;
}
.find-and-replace .btn {
  height: 30px;
}
.status {
  color: #a3a7cb;
}
.status-added {
  color: #b1ba17;
}
.status-ignored {
  color: #7278af;
}
.status-modified {
  color: #f2ab93;
}
.status-removed {
  color: #f293bc;
}
.status-renamed {
  color: #65a1ec;
}
.key-binding {
  display: inline-block;
  margin-left: 1px;
  padding: 0 7.5px;
  line-height: 2;
  font-family: 'Helvetica Neue', Arial, sans-serif;
  font-size: .8em;
  letter-spacing: 1px;
  border-radius: 3px;
  background-color: #282b47;
}
.list-group li {
  padding-left: 7.5px;
}
.list-group li.selected {
  color: #d3b1c0;
  background-color: #35395e;
}
.list-group li.selected:before {
  display: none;
}
.list-tree {
  color: #a3a7cb;
}
.list-tree .selected {
  color: #d3b1c0;
}
.select-list li {
  padding: 7.5px 15px;
}
.select-list .status {
  float: right;
}
.select-list.popover-list {
  padding: 7.5px;
  border-radius: 3px;
  background-color: #2b2f4d;
  box-shadow: 0 2px 8px 1px rgba(0, 0, 0, 0.3);
}
.select-list.popover-list .list-group {
  margin-top: 7.5px;
}
.select-list.popover-list .list-group li {
  padding-left: 7.5px;
}
.panel-heading {
  border-bottom: 1px solid #313557;
  background-color: #2a2d4a;
}
.inset-panel {
  background-color: #2a2d4a;
}
.inset-panel .panel-heading {
  background-color: #313557;
}
atom-panel.modal {
  border: 1px solid #313557;
  border-top: none;
  box-shadow: 0 2px 8px 1px rgba(0, 0, 0, 0.3);
}
.loading-spinner-large {
  display: block;
  width: 64px;
  height: 64px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 64px 64px;
}
.loading-spinner-large.inline-block {
  display: inline-block;
}
.loading-spinner-medium {
  display: block;
  width: 48px;
  height: 48px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 48px 48px;
}
.loading-spinner-medium.inline-block {
  display: inline-block;
}
.loading-spinner-small {
  display: block;
  width: 32px;
  height: 32px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 32px 32px;
}
.loading-spinner-small.inline-block {
  display: inline-block;
}
.loading-spinner-tiny {
  display: block;
  width: 16px;
  height: 16px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 16px 16px;
}
.loading-spinner-tiny.inline-block {
  display: inline-block;
}
.ui-site-1 {
  background-color: #182965;
}
.ui-site-2 {
  background-color: #264c29;
}
.ui-site-3 {
  background-color: #501708;
}
.ui-site-4 {
  background-color: #580980;
}
.ui-site-5 {
  background-color: #5a502d;
}
.status-bar {
  font-size: 12px;
  line-height: 2.5em;
  height: 2.5em;
  color: #a3a7cb;
  border-top: 1px solid #313557;
  background-color: #22253d;
}
.status-bar .inline-block {
  height: 100%;
  box-sizing: border-box;
  margin: 0;
  padding: 0 0.618em;
  vertical-align: top;
}
.status-bar .inline-block:hover {
  background: linear-gradient(transparent, #35395e);
  text-decoration: none;
}
.status-bar .inline-block .inline-block {
  padding: 0;
}
.status-bar .vk-terminal {
  border: 0;
  line-height: 0;
  margin: 0;
  padding: 0 0.618em;
  vertical-align: unset;
}
.status-bar .icon,
.status-bar [class*="icon"] {
  line-height: unset !important;
}
.status-bar .icon::before,
.status-bar [class*="icon"]::before {
  font-size: 1.2em;
  width: auto !important;
  height: 1em;
  line-height: 1;
  margin-right: 0.25em;
  top: auto;
}
.status-bar .vk-terminal .icon::before,
.status-bar status-bar-launch-mode.icon::before {
  margin-right: 0;
}
.status-bar .inline-git-diff-status div {
  padding: 0;
}
.status-bar a {
  padding-top: 0;
  padding-bottom: 0;
}
.status-bar a:hover {
  text-decoration: none;
  cursor: default;
}
.status-bar .status-bar-right {
  z-index: 10;
  padding-left: 0;
  box-shadow: -0.8em 0 0.6em #22253d;
}
.status-bar .status-bar-right .inline-block {
  margin: 0;
}
.tab-bar {
  position: relative;
  background-color: #22253d;
  border-bottom: 1px solid #313557;
}
.tab-bar .tab {
  padding: 0;
  margin: 0;
  flex: 1 1 0;
  -webkit-flex-flow: row nowrap;
  color: #a3a7cb;
  height: 30px;
  line-height: 30px;
  font-size: 12px;
  border-right: 1px solid #313557;
  background-color: #22253d;
  transition: max-width 0.2s ease-in-out;
}
.tab-bar .tab.active {
  padding-right: 0;
  flex: 1.618;
  color: #d3b1c0;
  background-color: #35395e;
}
.tab-bar .tab .title {
  margin: 0 0.618em;
  padding: 0 !important;
}
atom-dock .tab-bar .tab {
  max-width: 100%;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 50%;
  padding-right: 4px;
  margin-top: -3px;
  border-color: #65a1ec;
  transform: scale(1);
}
.tab-bar .tab.modified:hover .close-icon {
  color: currentColor;
}
.tab-bar .tab .close-icon {
  right: 0;
  padding-right: 7.5px;
}
.tab-bar .tab .close-icon::before {
  transform: scale(0);
}
.tab-bar .tab .close-icon:hover {
  color: #d3b1c0;
}
.tab-bar .tab.active .close-icon:hover {
  color: #a3a7cb;
}
.tab-bar .tab:hover .close-icon::before {
  transform: scale(1);
  transition-duration: 0.168s;
}
.tab-bar .tab.is-dragging {
  background: #10111c;
  border-color: transparent;
  opacity: 0.5;
}
.tab-bar .tab.is-dragging .close-icon {
  visibility: hidden;
}
.text-smaller {
  font-size: smaller;
}
.text-subtle {
  color: #7278af;
}
.text-highlight {
  color: #bea8ce;
}
.text-info {
  color: #65a1ec;
}
.text-success {
  color: #b1ba17;
}
.text-warning {
  color: #f2ab93;
}
.text-error {
  color: #f293bc;
}
.highlight {
  font-weight: 600;
  letter-spacing: 0.03em;
  padding: 3px 6px 5px;
  color: #bea8ce;
  border-radius: 3px;
  background-color: #2b2f4d;
}
.highlight-info {
  font-weight: 600;
  letter-spacing: 0.03em;
  padding: 3px 6px 5px;
  color: #bea8ce;
  border-radius: 3px;
  background-color: #785394;
}
.highlight-warning {
  font-weight: 600;
  letter-spacing: 0.03em;
  padding: 3px 6px 5px;
  color: #bea8ce;
  border-radius: 3px;
  background-color: #ab632d;
}
.highlight-error {
  font-weight: 600;
  letter-spacing: 0.03em;
  padding: 3px 6px 5px;
  color: #bea8ce;
  border-radius: 3px;
  background-color: #d64444;
}
.highlight-success {
  font-weight: 600;
  letter-spacing: 0.03em;
  padding: 3px 6px 5px;
  color: #bea8ce;
  border-radius: 3px;
  background-color: #55753c;
}
.background-message {
  font-weight: bold;
  color: #7278af;
}
.tooltip .tooltip-inner {
  line-height: 1;
  padding: .75em;
  white-space: nowrap;
  max-width: none;
}
.tooltip .keystroke {
  padding: .15em .4em;
  margin: 0 -0.3em 0 0.25em;
  border-radius: 2px;
  color: #d3b1c0;
  background: #35395e;
}
.tree-view-resizer {
  border-right: 1px solid #313557;
}
.tree-view {
  font-size: 12px;
  color: #a3a7cb;
  background-color: #22253d;
}
.tree-view .list-item.selected {
  color: #d3b1c0;
}
.scrollbars-visible-always ::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}
.scrollbars-visible-always ::-webkit-scrollbar-track {
  background: #35395e;
}
.scrollbars-visible-always ::-webkit-scrollbar-thumb {
  border-radius: 5px;
  border: 3px solid #35395e;
  background: #525992;
  background-clip: content-box;
}
.scrollbars-visible-always ::-webkit-scrollbar-corner {
  background: #35395e;
}
.scrollbars-visible-always ::-webkit-scrollbar-thumb:vertical:active {
  border-radius: 0;
  border-left-width: 0;
  border-right-width: 0;
}
.scrollbars-visible-always ::-webkit-scrollbar-thumb:horizontal:active {
  border-radius: 0;
  border-top-width: 0;
  border-bottom-width: 0;
}
.scrollbars-visible-always atom-text-editor ::-webkit-scrollbar-track {
  background: #35395e;
}
.scrollbars-visible-always atom-text-editor ::-webkit-scrollbar-corner {
  background: #35395e;
}
.scrollbars-visible-always atom-text-editor ::-webkit-scrollbar-thumb {
  border-color: #35395e;
  background: #525992;
}
.settings-view .package-card .meta {
  align-items: center;
}
.indent-guide-improved {
  background-color: #4d5388;
}
.indent-guide-improved.indent-guide-stack {
  background-color: #8a6583;
}
.indent-guide-improved.indent-guide-stack.indent-guide-active {
  background-color: #55753c;
}
.vim-mode-plus-search-container {
  padding: 7.5px;
}
.vim-mode-plus-search-container div {
  padding: 0;
}
.vim-mode-plus-search-container div.editor-container {
  padding-left: 3.75px;
}
