css:
/* Pallete to test new combinations with
	rgb(147,223,184)
	rgb(235,220,200)
	rgb(238,237,219)
	rgb(227,170,214)
	rgb(181,216,235)
	rgb(238,242,230)
	rgb(255,237,201)
	rgb(255,200,186)
	rgb(37,41,42)
	rgb(161,219,178)
	rgb(230,230,220)
	rgb(218,221,251)
	rgb(248,218,251)
	rgb(255,171,171)
	rgb(15,219,159)
	rgb(218,251,248)
	rgb(221,229,254)
	rgb(226,255,230)
*/
atom-dock.left .settings-view,
atom-dock.right .settings-view {
  flex-direction: column;
}
atom-dock.left .settings-view.pane-item,
atom-dock.right .settings-view.pane-item {
  background-color: #272a30;
}
atom-dock.left .settings-view .config-menu,
atom-dock.right .settings-view .config-menu {
  flex: none;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  padding: 5px;
  min-width: 0;
  max-width: none;
  border: none;
  border-bottom: 1px solid #1b1d22;
  background-color: inherit;
  overflow-x: initial;
}
atom-dock.left .settings-view .config-menu .nav,
atom-dock.right .settings-view .config-menu .nav {
  display: contents;
}
atom-dock.left .settings-view .config-menu .nav > li > a,
atom-dock.right .settings-view .config-menu .nav > li > a {
  padding: 0 .5em;
  border-radius: 3px;
}
atom-dock.left .settings-view .config-menu .nav .icon:before,
atom-dock.right .settings-view .config-menu .nav .icon:before {
  display: none;
}
atom-dock.left .settings-view .config-menu .button-area,
atom-dock.right .settings-view .config-menu .button-area {
  display: contents;
}
atom-dock.left .settings-view .config-menu .button-area .icon-link-external,
atom-dock.right .settings-view .config-menu .button-area .icon-link-external {
  font-size: 0;
  width: auto;
  margin: 0 5px;
  padding: 2.5px 5px;
  overflow: hidden;
}
atom-dock.left .settings-view .config-menu .button-area .icon-link-external:before,
atom-dock.right .settings-view .config-menu .button-area .icon-link-external:before {
  font-size: 16px;
  margin: 0;
}
atom-dock.left .settings-view .panels .panels-item,
atom-dock.right .settings-view .panels .panels-item {
  min-width: 100px;
}
atom-dock.left .settings-view .section,
atom-dock.right .settings-view .section,
atom-dock.left .settings-view .section:first-child,
atom-dock.right .settings-view .section:first-child,
atom-dock.left .settings-view .settings-view .section:last-child,
atom-dock.right .settings-view .settings-view .section:last-child {
  padding: 15px;
}
atom-dock.left .settings-view .sub-section:not(.collapsed) .package-container,
atom-dock.right .settings-view .sub-section:not(.collapsed) .package-container {
  padding-bottom: 0;
}
atom-dock.left .settings-view section .section-heading,
atom-dock.right .settings-view section .section-heading,
atom-dock.left .settings-view .section .section-heading,
atom-dock.right .settings-view .section .section-heading {
  font-size: 1.25em;
}
atom-dock.left .settings-view .sub-section .sub-section-heading,
atom-dock.right .settings-view .sub-section .sub-section-heading {
  font-size: 1.15em;
}
atom-dock.left .settings-view .sub-section .sub-section-heading.has-items::before,
atom-dock.right .settings-view .sub-section .sub-section-heading.has-items::before {
  margin-right: 0;
  font-size: 1.15em;
}
atom-dock.left .settings-view .setting-title,
atom-dock.right .settings-view .setting-title {
  font-size: 1.1em;
  font-weight: 500;
  color: #d8dadf;
}
atom-dock.left .settings-view .form-control,
atom-dock.right .settings-view .form-control {
  font-size: 1.1em;
}
atom-dock.left .settings-view .package-card,
atom-dock.right .settings-view .package-card {
  padding: 10px;
  font-size: inherit;
  background-color: #2c2f36;
}
atom-dock.left .settings-view .package-card:hover,
atom-dock.right .settings-view .package-card:hover {
  background-color: #282b32;
}
atom-dock.left .settings-view .package-card .package-name,
atom-dock.right .settings-view .package-card .package-name {
  font-size: inherit;
  font-weight: 600;
}
atom-dock.left .settings-view .package-card .package-version,
atom-dock.right .settings-view .package-card .package-version {
  font-size: .9em;
  color: rgba(159, 165, 177, 0.6);
  font-weight: 400;
}
atom-dock.left .settings-view .package-card .meta,
atom-dock.right .settings-view .package-card .meta {
  display: block;
}
atom-dock.left .settings-view .package-card .meta-user .avatar,
atom-dock.right .settings-view .package-card .meta-user .avatar {
  width: 20px;
  height: 20px;
}
atom-dock.left .settings-view .package-card .meta-user .author,
atom-dock.right .settings-view .package-card .meta-user .author {
  margin-left: .5em;
}
atom-dock.left .settings-view .package-card .btn-toolbar .btn-group,
atom-dock.right .settings-view .package-card .btn-toolbar .btn-group {
  display: flex;
  float: none;
}
atom-dock.left .settings-view .package-card .btn-toolbar .btn-group .btn,
atom-dock.right .settings-view .package-card .btn-toolbar .btn-group .btn {
  flex: 1 1 auto;
  overflow: hidden;
}
atom-dock.left .settings-view .package-card .btn-toolbar .btn-group .btn.status-indicator,
atom-dock.right .settings-view .package-card .btn-toolbar .btn-group .btn.status-indicator {
  flex: none;
  border: none;
}
atom-dock.left .settings-view .themes-panel .themes-picker-item,
atom-dock.right .settings-view .themes-panel .themes-picker-item {
  margin-top: 15px;
}
atom-dock.left .settings-view .themes-panel .theme-description,
atom-dock.right .settings-view .themes-panel .theme-description {
  margin: 5px 0;
}
